mapper.select:
select *
from question_state
where user_id = #{userId}
  and main_id = #{mainId}
  and sub_id = #{subId}

mapper.update:
update question_state
set is_starred = #{isStarred}
where user_id = #{userId}
  and main_id = #{mainId}
  and sub_id = #{subId}

update question_state
set state=#{state}
where user_id = #{userId}
  and main_id = #{mainId}
  and sub_id = #{subId}
